UPDATE locales_page_Text SET text_loc3='Schnee, blendend hell...$B$BMuss Stamm beschÃ¼tzen. Muss ihn stark machen. Muss stark sein.$B$BWeiÃŸ nicht...' WHERE entry=2413;
UPDATE locales_page_Text SET text_loc3='Feuerwasser.$B$BMuss mehr machen. Mehr Feuerwasser.$B$BFeuerwasser machen Winterfelle stark. Unsere Feinde besiegen.$B$BWinterfelle haben keine VerbÃ¼ndeten. Winterfelle sind stark. Haben groÃŸe Macht.' WHERE entry=2414;
