UPDATE locales_page_Text SET text_loc3='Simone die VerfÃ¼hrerin:$B$BFindet Simone beim Besudeln des Krater von Un\'Goros. Lasst Euch von Ihrer Verkleidung nicht tÃ¤uschen. NÃ¤hert Euch mit Bedacht und fordert sie zum Kampf heraus.' WHERE entry=2671;
