UPDATE locales_page_Text SET text_loc3='Solenor der SchlÃ¤chter:$B$BIn der trockenen Landschaft von Silithus lauert der Feind, Solenor der SchlÃ¤chter. Zweifellos verkleidet, definitiv eine Gefahr. NÃ¤hert Euch ihm und zwingt ihn zur Verwandlung.' WHERE entry=2673;
