UPDATE locales_page_Text SET text_loc3='Ihr werdet morgen jemand ganz besonderes treffen, also denkt daran: man macht sich Freunde, indem man nett ist. Nicht indem man sie totprÃ¼gelt!' WHERE entry=3013;
