UPDATE locales_page_Text SET text_loc3='Ihr seid etwas Besonderes. Ganz gleich was Eure Freunde, Verwandte, Kollegen, irgendwelche Fremde oder Euer Gewissen sagen mÃ¶gen.' WHERE entry=3016;
UPDATE locales_page_Text SET text_loc3='Wenn alles scheitert ist es Zeit zu verschwinden.' WHERE entry=3017;
