UPDATE locales_quest set title_loc3='Galgars Kaktusapfel Surprise',details_loc3='Hier im Tal der Prüfungen wird es ganz schön heiß.$B$B<Galgar wischt sich den Schweiß von der Stirn.>$B$BWenn ich doch nur ein paar Kaktusäpfel hätte, dann könnte ich meinen berühmten Kaktusapfel Surprise machen! Nichts kühlt einen schneller ab als ein Stück dieser leckeren Spezialität.$B$BIch sag Euch was, $n. Wenn Ihr mir 6 Kaktusäpfel bringt, dann mache ich Euch ein paar Portionen Kaktusapfel Surprise, die Ihr mit auf Eure Abenteuer nehmen könnt. Falls Ihr daran interessiert seid, findet Ihr Kaktusäpfel bei den Kaktuspflanzen, die hier in der Gegend wachsen.',objectives_loc3='Bringt Galgar 6 Kaktusäpfel. Ihr erinnert Euch doch, dass er gesagt hat, man fände sie in der Nähe von Kakteen?',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=4402;
