UPDATE locales_quest set title_loc3='GESUCHT: ZWERGE!',details_loc3='Auf Befehl von Unteroffizier Yazra Murrblut sind alle Zwerge auf dem Schlachtfeld umgehend zu töten.$B$BSolltet Ihr einen Zwerg im Kampf töten, schneidet ihm sein Rückgrat heraus und lasst den rückgratlosen Fleischhaufen als Mahnmal für alle zurück, die sich gegen die Frostwölfe stellen.$B$BKehrt anschließend mit dem Beweis Eurer Heldentat zu Unteroffizier Yazra Murrblut zurück.',objectives_loc3='Ihr wurdet damit beauftragt, alle feindlichen Zwergenspieler im Alteractal zu töten.$B$BTötet einen Zwerg und schneidet ihm sein Rückgrat heraus. Bringt anschließend das Zwergenrückgrat zu Unteroffizier Yazra Murrblut in Burg Frostwolf.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=7401;
UPDATE locales_quest set title_loc3='GESUCHT: ORCS!',details_loc3='Auf Befehl von Korporal Noreg Sturmlanze sind alle verfügbaren Soldaten dazu aufgefordert, jeden gegnerischen Orc auf dem Schlachtfeld umgehend zu töten.$B$BDie Soldaten sind weiterhin dazu aufgefordert, einen Hauer oder Zahn aus dem Maul des Feindes zu entfernen, um diese als Beweis für ihre Tat vorzeigen zu können.$B$BBitte tragt dafür Schutzhandschuhe.',objectives_loc3='Ihr wurdet damit beauftragt, alle feindlichen orcischen Spieler im Alteractal zu töten.$B$BTötet einen Orc und bringt seinen Zahn zurück zu Korporal Noreg Sturmlanze in Dun Baldar.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=7402;
