UPDATE locales_quest set title_loc3='Meister der Arena',details_loc3='Um der Meister der Arena zu werden, bedarf es bestimmt noch weiterer Taten! Vielleicht weiß der Pirat, der das Ereignis in Gurubashi veranstaltet, mehr.',objectives_loc3='Begebt Euch in die Arena des Schlingendorntals und sprecht mit Störtebrecher.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=7810;
