UPDATE locales_quest set title_loc3='Euer Schicksal erwartet Euch...',details_loc3='"Euer Schicksal erwartet Euch im Osttal."$B$BDas Horoskop, das Sayge Euch überreicht hat, fühlt sich beim Berühren leicht warm an. Haltet Ihr es etwas fester in der Hand, so seht Ihr das Holzfällerlager des Osttals im Wald von Elwynn vor Euch. Beim Fokussieren auf dieses Bild, seht Ihr einen Heuhaufen neben einem Stall.',objectives_loc3='Reist zum Holzfällerlager des Osttals im Wald von Elwynn und sucht nach Eurem Schicksal.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=7937;
UPDATE locales_quest set title_loc3='Euer Schicksal erwartet Euch...',details_loc3='"Euer Schicksal erwartet Euch in den Todesminen."$B$BDas Horoskop, das Sayge Euch überreicht hat, fühlt sich beim Berühren leicht warm an. Haltet Ihr es etwas fester in der Hand, so seht Ihr die Todesminen in Westfalls Ruinen von Mondbruch. Beim Fokussieren auf dieses Bild, windet Ihr Euch durch verwinkelte Passagen bis Ihr ein schimmerndes Portal erreicht... eines, das in das Herz der Minen selbst führt. Beim Durchschreiten taucht eine seltsame Truhe aus dem Nichts auf.',objectives_loc3='Reist zu den Todesminen in Westfall und sucht nach Eurem Schicksal.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=7938;
