UPDATE locales_quest set title_loc3='Euer Schicksal erwartet Euch...',details_loc3='"Euer Schicksal erwartet Euch in den Höhlen des Wehklagens."$B$BDas Horoskop, das Sayge Euch überreicht hat, fühlt sich beim Berühren leicht warm an. Haltet Ihr es etwas fester in der Hand, so seht Ihr eine Höhle im Südwesten des Wegekreuzes im Brachland. Beim Fokussieren auf dieses Bild, windet Ihr Euch durch gewundene Passagen bis Ihr ein schimmerndes Portal erreicht... eines, das in das Herz der Höhlen selbst führt. Beim Durchschreiten taucht eine seltsame Truhe aus dem Nichts auf.',objectives_loc3='Reist zu den Höhlen des Wehklagens im Brachland und sucht nach Eurem Schicksal.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=7944;
UPDATE locales_quest set title_loc3='Euer Schicksal erwartet Euch...',details_loc3='"Euer Schicksal erwartet Euch außerhalb des Bleichmähnenfelsens."$B$BDas Horoskop, das Sayge Euch überreicht hat, fühlt sich beim Berühren leicht warm an. Haltet Ihr es etwas fester in der Hand, so seht Ihr den Bleichmähnenfels von Mulgore. Beim Fokussieren auf dieses Bild seht Ihr einen Baumstumpf in der Nähe des Höhleneingangs.',objectives_loc3='Reist zum Bleichmähnenfels in Mulgore und sucht nach Eurem Schicksal.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=7945;
