UPDATE locales_quest set title_loc3='Das Juwel von Kajaro',details_loc3='',objectives_loc3='',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8104;
UPDATE locales_quest set title_loc3='Die Schlacht um das Arathibecken!',details_loc3='$n, die Schlacht im Arathibecken ist ein Kampf um Ressourcen und Territorien. Um unsere Truppen mit den dringend benötigten Rohstoffen und Vorräten zu versorgen, gilt es bestimmte Schlüsselpositionen zu besetzen und deren Ressourcen zu nutzen. Ich erteile Euch hiermit den Auftrag, die von unserem Stützpunkt am weitesten entfernten Schlüsselpositionen anzugreifen.$B$BErobert den Hof, die Mine, das Sägewerk und die Schmiede, sofern der Feind diese Positionen unter seiner Kontrolle hat. Sucht an jedem dieser Orte nach dem gegnerischen Banner und reißt es aus seiner Halterung!$B$BMeldet Euch wieder bei mir, wenn Euch das an allen vier Schlüsselpositionen gelungen ist.',objectives_loc3='Erobert die Mine, das Sägewerk, die Schmiede und die Farm und meldet Euch anschließend bei Feldmarschall Lichtmark in der Zuflucht.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8105;
