UPDATE locales_quest set title_loc3='Die Schädelsammlung',details_loc3='Es ist wahr. Hakkar hat sich in Zul\'Gurub eingenistet und badet in den Kräften unserer Götter. Von ihm kontrollierte Priester haben damit begonnen, die Energien unserer heiligsten Gottheiten auf den Seelenschinder zu kanalisieren. Seine Macht wird schon bald die aller Wesen in Azeroth übersteigen. Er muss aufgehalten werden!$B$BHier, nehmt diese heilige Kordel. Findet die Priester, die es wagen die Energien unserer Götter zu kanalisieren, und reiht ihre Köpfe auf dieser Kordel aneinander.$b$bKehrt anschließend zu mir zurück.',objectives_loc3='Reiht die Köpfe der 5 Kanalisierer auf der heiligen Kordel aneinander. Bringt dann die Trollschädelsammlung zu Exzhal auf der Insel Yojamba.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8201;
