UPDATE locales_quest set title_loc3='Nats Maßband',details_loc3='Inmitten von zerbrochenen Angelruten und anderen Geräten, liegt ein zusammengerolltes, seidenes Maßband. An dem Band hängt ein Schild mit der Aufschrift:$B$B"Nat Pagle, die Düstermarschen."',objectives_loc3='Bringt Nats Maßband zu Nat Pagle in den Düstermarschen zurück.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8227;
