UPDATE locales_quest set title_loc3='Ein Schmuckstück für Zanza',details_loc3='Die Zerstörung der zahlreichen Schmuckstücke der Hakkari ist für uns von großer Bedeutung. Wir heißen sogar Außenseiter in unserem Stamm willkommen, um uns dabei in eigener Sache zu helfen!$B$BBringt, was auch immer für ein Schmuckstück ihr in Zul\'Gurub finden solltet, zum Altar von Zanza und zerstört es. Ich werde Euch nur für dieses eine Mal eine zusätzliche Ehrenmünze unseres Stammes geben; dies liegt hoch über dem, was Ihr normalerweise erhalten würdet.$B$BWerdet selbst Zeuge des Segens von Zanza!',objectives_loc3='Zerstört jedes Schmuckstück der Hakkari, das Ihr in Zul\'Gurub findet, am Altar von Zanza auf der Insel Yojamba. Sprecht ganz in der Nähe mit Vinchaxa, sobald Ihr dies getan habt.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8240;
