UPDATE locales_quest set title_loc3='Kampf um die Kriegshymnenschlucht',details_loc3='Die Schildwachen der Silberschwingen sind auf Grund der Zerstörung, die die Orcs im Wald anrichten, im Krieg mit der Vorhut des Kriegshymnenklans. Allerdings gibt es noch andere Gründe, als die bloße Liebe zu der Natur, für die Verteidigung von ausgerechnet diesem Wald.$B$BDer Wald bildet eine strategische Barriere, die es einfach macht, das Eschental gegen jegliche Art von Großangriffen zu verteidigen. Ohne den Wald, würde Astranaar lediglich ein oder zwei Tage standhalten, bevor es vom Brachland einverleibt werden würde.$B$BSeid tapfer und schlagt Euch gegen die Vorhut des Kriegshymnenklans, $n! Für die Allianz!',objectives_loc3='Bringt 3 Ehrenabzeichen der Kriegshymnenschlucht zu einem Brigadegeneral der Allianz, außerhalb der Schlachtfelder.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8399;
