UPDATE locales_quest set title_loc3='Eroberung des Arathibeckens',details_loc3='Unser Konflikt mit der Allianz hat sich zu einem richtigen Krieg entwickelt. Es ist nicht länger notwendig unter falschen Vorwänden in die Schlacht zu ziehen, keine Seite erwartet dies noch länger.$B$BIn der Schlacht um das Arathibecken geht es nur um eines, Ressourcen. Nicht mehr und nicht weniger. Entweder die Allianz erobert diese Ressourcen und benutzt sie gegen uns, oder wir kommen ihnen zuvor und verwenden die dortigen Ressourcenquellen für unsere Kriegspläne. $n, helft den Entweihten dabei, die von uns benötigten Ressourcenlieferungen aufrechtzuerhalten!',objectives_loc3='Bringt 3 Ehrenabzeichen des Arathibeckens zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8436;
