UPDATE locales_quest set title_loc3='Ziel: Tunnelgräber des Zoraschwarms',details_loc3='Es wurde Verstärkung für den Angriff auf den Bau des Zoraschwarms angefordert. Man hat Euch ausgewählt, um die Tunnelgräber des Zoraschwarms anzugreifen. Erstattet bei Kommandant Mar\'alith Bericht, sobald Ihr Eure Aufgabe beendet habt.',objectives_loc3='Tötet 30 Tunnelgräber des Zoraschwarms und erstattet anschließend Bericht bei Kommandant Mar\'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz VII, um diese Aufgabe abschließen zu können.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8687;
