UPDATE locales_quest set title_loc3='Schleier der unendlichen Weisheit',details_loc3='Ich freue mich, dass Ihr uns bei unserem Angriff auf Ahn\'Qiraj unterstützen wollt, $n. Ich kann einen schützenden Gegenstand für Euch herstellen, der Euch im Kampf gegen unseren Feind behilflich sein wird, $C.$B$BIch benötige dafür ein Tuch der Qiraji, das ich dann natürlich für Euch anpassen werde. Außerdem bräuchte ich noch Götzen und Skarabäen, die ich zu einer passenden Schnalle verarbeiten möchte.$B$BIhr werdet bestimmt nicht enttäuscht sein.',objectives_loc3='Bringt 1 Kampftuch der Qiraji, 2 Jaspisgötzen, 5 Goldskarabäen und 5 Tonskarabäen zu Keyl Flinkklaue in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen respektvollen Ruf beim Zirkel des Cenarius.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8689;
UPDATE locales_quest set title_loc3='Umhang der aufziehenden Stürme',details_loc3='Ich freue mich, dass Ihr uns bei unserem Angriff auf Ahn\'Qiraj unterstützen wollt, $n. Ich kann einen schützenden Gegenstand für Euch herstellen, der Euch im Kampf gegen unseren Feind behilflich sein wird, $C.$B$BIch benötige dafür ein Tuch der Qiraji, das ich dann natürlich für Euch anpassen werde. Außerdem bräuchte ich noch Götzen und Skarabäen, die ich zu einer passenden Schnalle verarbeiten möchte.$B$BIhr werdet bestimmt nicht enttäuscht sein.',objectives_loc3='Bringt 1 Hoheitstuch der Qiraji, 2 Obsidiangötzen, 5 Tonskarabäen und 5 Goldskarabäen zu Keyl Flinkklaue in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen respektvollen Ruf beim Zirkel des Cenarius.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8690;
UPDATE locales_quest set title_loc3='Tuch der behüteten Geheimnisse',details_loc3='Ich freue mich, dass Ihr uns bei unserem Angriff auf Ahn\'Qiraj unterstützen wollt, $n. Ich kann einen schützenden Gegenstand für Euch herstellen, der Euch im Kampf gegen unseren Feind behilflich sein wird, $C.$B$BIch benötige dafür ein Tuch der Qiraji, das ich dann natürlich für Euch anpassen werde. Außerdem bräuchte ich noch Götzen und Skarabäen, die ich zu einer passenden Schnalle verarbeiten möchte.$B$BIhr werdet bestimmt nicht enttäuscht sein.',objectives_loc3='Bringt 1 Kampftuch der Qiraji, 2 Alabastergötzen, 5 Steinskarabäen und 5 Kristallskarabäen zu Keyl Flinkklaue in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen respektvollen Ruf beim Zirkel des Cenarius.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8691;
