UPDATE locales_quest set title_loc3='Ring der unendlichen Weisheit',details_loc3='Man sagt, dass die Ringe, die von den Leutnants der Qiraji getragen werden, ungeheure Fähigkeiten verleihen. Die verderbten Edelsteine, mit denen sie verziert sind, könnten theoretisch mit reinerem Material ausgetauscht werden, um unbefleckte Versionen der Ringe herzustellen.$B$BDie Edelsteine, die die Götzen der Qiraji schmücken, würden sich dafür gut eignen, da sie von besserer Qualität sind.$B$BBringt mir den Ring und Götzen und ich werde einen mächtigen Ring für Euch anfertigen, $C.',objectives_loc3='Bringt 1 Zeremonienring der Qiraji, 2 Obsidiangötzen, 5 Silberskarabäen und 5 Knochenskarabäen zu Windruferin Yessendra in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf beim Zirkel des Cenarius.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8697;
