UPDATE locales_quest set title_loc3='Hammer der unendlichen Weisheit',details_loc3='Die Qiraji und ihre Diener besitzen Gegenstände, die aus dem seltensten und feinsten Material hergestellt werden: Knochen von riesengroßen uralten Kreaturen, Edelsteine aus den Tiefen der Erde, reines und starkes Metall.$B$BAllein der Anblick dieser Qirajiartefakte erfüllt mich mit dem Drang, mein Handwerk an ihren zerschmetterten und geschmolzenen Stücken auszuüben. Beweist dem Zirkel Eure Loyalität, $n, und ich werde aus den feinsten Qirajibestandteilen eine mächtige Waffe für Euch herstellen.',objectives_loc3='Bringt 1 verschnörkelten Griff der Qiraji, 2 züngelnde Götzen, 5 Bronzeskarabäen und 5 Elfenbeinskarabäen zu Aufseher Haro in Silithus. Diese Aufgabe erfordert zum Abschluss auch einen ehrfürchtigen Ruf beim Zirkel des Cenarius.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8705;
