UPDATE locales_quest set title_loc3='Metzen das Rentier',details_loc3='Es ist eine KATASTROPHE! Metzen das Rentier wurde entführt!$B$BMetzen ist eines von Altvater Winters acht Rentieren - und Eigentum von Kokelwälder. Wir glauben, dass dieser schreckliche, monströse Griesgram, der sich im Vorgebirge des Hügellandes versteckt, ihn entführt hat. Gerade jetzt zur Festzeit können wir hier nicht weg. Bitte findet Metzen, besiegt seine Entführer und bringt ihn zu uns zurück!',objectives_loc3='Befreit Metzen das Rentier aus den Klauen seiner Entführer nahe der Eisfellhöhle im Vorgebirge des Hügellandes.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8746;
