UPDATE locales_quest set title_loc3='Das Mondfest',details_loc3='Es ist mal wieder soweit, $n! Der Zirkel des Cenarius lädt alle Bewohner Azeroths dazu ein, gemeinsam mit uns, das Mondfest zu feiern. Der Ort, an dem Ihr die örtliche Botin des Mondfests finden könnt, wird durch einen Kreis aus heiligem Mondlicht markiert. Sie vermag Euch ausführlicher über dieses Fest zu berichten.',objectives_loc3='Sprecht mit der Botin des Mondfests auf der Anhöhe der Ältesten in Donnerfels. Die Boten in den anderen Hauptstädten können Euch ebenfalls weiterhelfen.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8875;
