UPDATE locales_quest set title_loc3='Ein aufrichtiges Angebot',details_loc3='Ich grüße Euch, $C. Eure Haltung verrät mir, dass Ihr bereits ganz schön herumgekommen seid. Ich würde allerdings darauf wetten, dass Euch noch keine Rüstung wie diese untergekommen ist.$B$BWenn Ihr mir einen kleinen Gefallen tut, wäre ich bereit, diese Rüstung gegen ein normales Paar Armschienen einzutauschen.$B$BIch bräuchte eine ziemlich große Menge an Giftproben von den Spinnen und Skorpionen aus Silithus. Bringt es mir zusammen mit einigen Goldmünzen und ich werde den Tausch vornehmen.',objectives_loc3='Sammelt 15 Giftproben aus Silithus und bringt sie zusammen mit 20 Gold und einem Paar Armschienen des Wildherzens zu Mokvar in Orgrimmar zurück.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=8913;
