UPDATE locales_quest set title_loc3='Das Beste gibt\'s zum Schluss',details_loc3='Das ist ja eine unglaubliche Geschichte, $n! Seid Euch meines Dankes gewiss, und der aufrichtigen Dankbarkeit all derer, die in dieses Abenteuer verwickelt waren. Ihr habt durch Eure Taten die Sünden unserer Vergangenheit gesühnt.$B$BNun zu Eurer Belohnung. Ich habe hier einige Dinge, an denen Ihr gewiss Gefallen finden werdet.',objectives_loc3='Gebt Mokvar Euren Helm der Ehre und Eure Brustplatte der Ehre.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=9013;
