UPDATE locales_quest set title_loc3='Tunika der Erlösung',details_loc3='Ihr werdet die Überbleibsel von unseren gefallenen Helden bei den Körpern der Obersten von Naxxramas finden. Bringt mir diese entweihten Vermächtnisse, kampfzerschlissene Plattenstücke und die Handwerksmaterialien, die zum Wiederherstellen der verfluchten Rüstung benötigt werden, damit ich Euch eine prächtige Ausstattung anfertigen kann.',objectives_loc3='Kommandant Eligor Morgenbringer bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird für Euch eine Tunika der Erlösung anfertigen, wenn Ihr ihm folgende Gegenstände bringt: 1 entweihte Tunika, 25 kampfzerschlissene Plattenstücke, 4 Arkanitbarren und 3 geschmeidige unverwüstliche Bälge.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=9043;
