UPDATE locales_quest set title_loc3='Brustplatte der Knochensense',details_loc3='Wenn Ihr etwas von der Knochensensenrüstung wollt, dann werdet Ihr die Materialien dafür auftreiben müssen.$B$BGeht nach Naxxramas, der schwebenden Metropole des Todes, hier oben im Himmel, und sucht nach entweihter Rüstung sowie kampfzerschlissenen Rüstungsstücken. Bringt alles mitsamt der Materialien zum Zusammenfügen zurück und Ihr bekommt Eure Knochensensenrüstung. Oh, Ihr werdet mich natürlich auch für meine Mühen entschädigen müssen... Ich stelle die Knochen zur Verfügung.',objectives_loc3='Rohan der Assassine bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird für Euch eine Brustplatte der Knochensense anfertigen, wenn Ihr ihm folgende Gegenstände bringt: 1 entweihte Brustplatte, 25 kampfzerschlissene Lederstücke, 2 Arkanitbarren und 6 geschmeidige unverwüstliche Bälge.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=9077;
UPDATE locales_quest set title_loc3='Beinplatten der Knochensense',details_loc3='Wenn Ihr etwas von der Knochensensenrüstung wollt, dann werdet Ihr die Materialien dafür auftreiben müssen.$B$BGeht nach Naxxramas, der schwebenden Metropole des Todes, hier oben im Himmel, und sucht nach entweihter Rüstung sowie kampfzerschlissenen Rüstungsstücken. Bringt alles mitsamt der Materialien zum Zusammenfügen zurück und Ihr bekommt Eure Knochensensenrüstung. Oh, Ihr werdet mich natürlich auch für meine Mühen entschädigen müssen... Ich stelle die Knochen zur Verfügung.',objectives_loc3='Rohan der Assassine bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird für Euch Beinplatten der Knochensense anfertigen, wenn Ihr ihm folgende Gegenstände bringt: 1 Paar entweihte Beinplatten, 20 kampfzerschlissene Lederstücke, 1 Arkanitbarren, 8 geschmeidige unverwüstliche Bälge und 100 Goldstücke.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=9078;
