UPDATE locales_quest set title_loc3='Robe des Glaubens',details_loc3='<Vater Montoy vollführt vor Euch ein paar merkwürdige Gesten in der Luft.>$B$B$g Gesegneter Junge:Gesegnetes Mädchen;, habt Ihr die Gewänder der Gefallenen gefunden? Die armen Seelen, die ihre letzten Atemzüge auf dem Schlachtfeld ausgehaucht haben - um alles beraubt, einschließlich ihrer Würde.$B$BBringt mir die entweihten Überbleibsel der Verstorbenen sowie die Reagenzien zur Läuterung... auf dass Ihr mit Glauben erfüllt werdet.',objectives_loc3='Vater Inigo Montoy bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird für Euch eine Robe des Glaubens anfertigen, wenn Ihr ihm folgende Gegenstände bringt: 1 entweihte Robe, 25 kampfzerschlissene Stoffstücke, 4 Einheiten Mondstoff und 2 Nexuskristalle.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=9111;
