UPDATE locales_quest set title_loc3='Der Greuelwächter - Arkanum des Schutzes',details_loc3='Zanza kann viele antike Trollverzauberungen mit den richtigen Komponenten herstellen! Habt Ihr von dem Greuelwächter gehört? Nein, natürlich nicht. Euer Geist wurde mit jämmerlicher Elfenmagie verwirrt.$B$BBringt mir ein Arkanum, wie es die Elfen herstellen, damit ich das Böse zerstören kann. Seid Ihr erfolgreich, so soll der Greuelwächter Euch gehören.',objectives_loc3='Zanza der Ruhelose in Zul\'Gurub möchte ein Arkanum des Schutzes.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=9208;
UPDATE locales_quest set title_loc3='Der Greuelwächter - Arkanum der Schnelligkeit',details_loc3='Zanza kann viele antike Trollverzauberungen mit den richtigen Komponenten herstellen! Habt Ihr von dem Greuelwächter gehört? Nein, natürlich nicht. Euer Geist wurde mit jämmerlicher Elfenmagie verwirrt.$B$BBringt mir ein Arkanum, wie es die Elfen herstellen, damit ich das Böse zerstören kann. Seid Ihr erfolgreich, so soll der Greuelwächter Euch gehören.',objectives_loc3='Zanza der Ruhelose in Zul\'Gurub möchte ein Arkanum der Schnelligkeit.',offerrewardtext_loc3='',requestitemstext_loc3='',endtext_loc3='',objectivetext1_loc3='',objectivetext2_loc3='',objectivetext3_loc3='',objectivetext4_loc3='' where entry=9209;
